package com.CarManagementSystem.TrainingMug.dto;import jakarta.validation.constraints.*;import java.time.LocalDate;public class CarDTO {    private Long id;    @NotBlank(message = "Name is required")    @Size(min = 2, max = 100, message = "Name must be between 2 and 100 characters")    private String name;    @NotBlank(message = "Model is required")    @Size(min = 2, max = 50, message = "Model must be between 2 and 50 characters")    private String model;    @PastOrPresent(message = "Year must be in the past or present")    private LocalDate year;    @NotNull(message = "Price is required")    @Positive(message = "Price must be a positive value")    @Max(value = 100_000_000, message = "Price must not exceed 100,000,000")    private Integer price;    @NotBlank(message = "Color is required")    @Size(max = 30, message = "Color must not exceed 30 characters")    private String color;    @NotBlank(message = "Fuel type is required")    @Pattern(regexp = "Petrol|Diesel|Electric|Hybrid",            message = "Fuel type must be one of: Petrol, Diesel, Electric, Hybrid")    private String fuelType;    public CarDTO()    {    }    public CarDTO(Long id, String name, String model, LocalDate year, Integer price, String color, String fuelType) {        this.id = id;        this.name = name;        this.model = model;        this.year = year;        this.price = price;        this.color = color;        this.fuelType = fuelType;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getModel() {        return model;    }    public void setModel(String model) {        this.model = model;    }    public LocalDate getYear() {        return year;    }    public void setYear(LocalDate year) {        this.year = year;    }    public Integer getPrice() {        return price;    }    public void setPrice(Integer price) {        this.price = price;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    public String getFuelType() {        return fuelType;    }    public void setFuelType(String fuelType) {        this.fuelType = fuelType;    }}