package com.CarManagementSystem.TrainingMug.repositories;import com.CarManagementSystem.TrainingMug.entities.CarEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface CarRepository extends JpaRepository<CarEntity, Long> {    @Query("SELECT c FROM CarEntity c WHERE " +            "LOWER(c.name) LIKE LOWER(CONCAT('%', :query, '%')) OR " +            "LOWER(c.model) LIKE LOWER(CONCAT('%', :query, '%')) OR " +            "CAST(c.year AS string) LIKE CONCAT('%', :query, '%') OR " +            "LOWER(c.color) LIKE LOWER(CONCAT('%', :query, '%')) OR " +            "LOWER(c.fuelType) LIKE LOWER(CONCAT('%', :query, '%'))")    List<CarEntity> searchCars(@Param("query") String query);}